const priorityOrder = {
  HIGH: 0,
  MEDIUM: 1,
  LOW: 2
};

const filteredEntries = $input.all().map(item=>item.json).filter(item => item.URL && /^(QUEUE)$/.test(item.STATUS)).toSorted((a, b) => {
  const aPriority = priorityOrder[a.PRIORITY] ?? 3;
  const bPriority = priorityOrder[b.PRIORITY] ?? 3;

  if (aPriority !== bPriority) {
    return aPriority - bPriority; // sort by priority
  }

  return a.row_number - b.row_number; // sort by row_number ascending
});

//console.log(filteredEntries.slice(0))

return filteredEntries.slice(0, 1);

const timeThreshold = 10 * 24 * 60 * 60 * 1000;

const now = new Date();
const thresholdDate = new Date(now.getTime() - timeThreshold);

const filteredEntries = $input.all().map(item=>item.json)
  .filter(item => item.URL && /^(IN_PROGRESS|WAITING_APPROVAL)$/.test(item.STATUS))
  .filter(item => {
    if (!item.UPDATE_DATE) return false;
    const updateDate = new Date(item.UPDATE_DATE);
    return !isNaN(updateDate) && updateDate < thresholdDate;
  })

//console.log(filteredEntries)

return filteredEntries;

